script:
frappe.ui.form.on('Work Order', {
    refresh(frm) {
        toggleFields(frm);
    },
    sales_order(frm) {
        toggleFields(frm);
    },
    custom_fg_sales_order(frm) {
        toggleFields(frm);
    }
});

function toggleFields(frm) {
    if (frm.doc.sales_order) {
        frm.toggle_display('custom_fg_sales_order', false);
    } else {
        frm.toggle_display('custom_fg_sales_order', true);
    }

    if (frm.doc.custom_fg_sales_order) {
        frm.toggle_display('sales_order', false);
    } else {
        frm.toggle_display('sales_order', true);
    }
}
